function MessageBoxConfirm(n,t){return $.MessageBox({buttonDone:"Yes",buttonFail:"No",speed:250,top:"35%",width:"auto",queue:!1,title:t,message:n})}function MessageBoxAlert(n,t){return $.MessageBox({speed:250,top:"40%",title:t,width:"auto",queue:!1,message:n})}function TryParseInt(n,t){var i=t;return n!==null&&n.length>0&&(isNaN(n)||(i=parseInt(n))),i}function error_handler(n){if(n.errors){var t="";$.each(n.errors,function(n,i){"errors"in i&&$.each(i.errors,function(){t+=this+"<br/>"})});MessageBoxAlert(t,"Error");RefreshGrid(n)}}function error_handler_norefresh(n){if(n.errors){var t="";$.each(n.errors,function(n,i){"errors"in i&&$.each(i.errors,function(){t+=this+"<br/>"})});MessageBoxAlert(t,"Error")}}function RefreshGrid(){$("#grid").data("kendoGrid").dataSource.read()}